import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      naviBtnBackgrounds: Array(1).fill('#282c34')
    }
  }

  renderNaviBtn(i, name) {
    return (<li><NaviBtn
        value = {name}
        background = {this.state.naviBtnBackgrounds[i]}
    /></li>)
  }

  renderNaviBar() {
    return (
        <ul>
          {this.renderNaviBtn(1, '主页')}
        </ul>
    )
  }

  render() {
    return (
        <div className="App">
          <header className="App-header">
            {/*<img src={logo} className="App-logo" alt="logo" />*/}
            <p>
              Jarvis Workstation
            </p>
          </header>
          <body>
          <div className="NaviBar">
            {this.renderNaviBar()}
          </div>
          </body>
        </div>
    );
  }
}

class NaviBtn extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      background: '#282c34',
    }
  }

  activate() {
    this.setState({background: 'rgba(240,248,255, 0.3)'})
  }

  deactivate() {
    this.setState({background: '#282c34'})
  }

  render() {
    return (
        <div className='NaviBtn'>
          <button
              style={{ background: this.state.background }}
          >{this.props.value}</button>
        </div>
    )
  }
}

export default App;
