import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      naviBtnBackgrounds: Array(1).fill('#282c34'),
      chosenNavi: 0,
    }
  }

  // Navi Bar
  naviActivate(i) {
    const list = this.state.naviBtnBackgrounds.slice();
    list[i] = 'rgba(240,248,255, 0.3)';
    this.setState({
      naviBtnBackgrounds: list,
    })
  }

  naviDeactivate(i) {
    const list = this.state.naviBtnBackgrounds.slice();
    list[i] = '#282c34';
    this.setState({
      naviBtnBackgrounds: list,
    })
  }

  handleNaviClick(i) {
    this.state.naviBtnBackgrounds.map(
        (value, index) => {
          if (index === i) {
            this.naviActivate(i)
          } else {
            this.naviDeactivate(index)
          }
        }
    );
  }

  renderNaviBtn(i, name) {
    return (<li><NaviBtn
        value = {name}
        background = {this.state.naviBtnBackgrounds[i]}
        onClick
    /></li>)
  }

  renderNaviBar() {
    return (
        <ul>
          {this.renderNaviBtn(0, '主页')}
        </ul>
    )
  }

  render() {
    return (
        <div className="App">
          <header className="App-header">
            {/*<img src={logo} className="App-logo" alt="logo" />*/}
            <p>
              Jarvis Workstation
            </p>
          </header>
          <body>
          <div className="NaviBar">
            {this.renderNaviBar()}
          </div>
          </body>
        </div>
    );
  }
}

class NaviBtn extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
        <div className='NaviBtn'>
          <button
              style = {{background: this.props.background}}
          >{this.props.value}</button>
        </div>
    )
  }
}

export default App;
