import React from 'react';
import logo from './logo.svg';
import './App.css';

const NAVI_ACTIVE_COLOR = 'rgba(240,248,255, 0.3)';
const NAVI_DEACTIVE_COLOR = '#282c34';
const NAVI_HOVER_COLOR = '#555';

class App extends React.Component {


  constructor(props) {
    super(props);

    let list = Array(10).fill('#282c34');
    list[0] = 'rgba(240,248,255, 0.3)';

    this.state = {
      naviBtnBackgrounds: this.initNaviList(),
      chosenNavi: 0,
    };
    //his.initState();
  }

  initNaviList() {
    let list = Array(10).fill('#282c34');
    list[0] = 'rgba(240,248,255, 0.3)';
    return list;
  }

  initState() {
    let list = this.state.naviBtnBackgrounds.slice();
    list[0] = 'rgba(240,248,255, 0.3)';
    this.setState({naviBtnBackgrounds: list});
    console.log(this.state);
  };

  // Navi Bar
  handleNaviClick(i) {
    const list = this.state.naviBtnBackgrounds.slice();
    list.forEach(
        (value, index) => {
          if (index === i) {
            list[index] = 'rgba(240,248,255, 0.3)';
          } else {
            list[index] = '#282c34';
          }
        }
    );
    this.setState({
      naviBtnBackgrounds: list,
      chosenNavi: i,
    })
  }

  renderNaviBtn(i, name) {
    return (<li><NaviBtn
        value = {name}
        background = {this.state.naviBtnBackgrounds[i]}
        activate = {this.state.chosenNavi === i}
        onClick = {() => this.handleNaviClick(i)}
    /></li>)
  }

  renderNaviBar() {
    return (
        <ul>
          {this.renderNaviBtn(0, '主页')}
          {this.renderNaviBtn(1, '测试工作台')}
        </ul>
    )
  }

  render() {
    return (
        <div className="App">
          <header className="App-header">
            {/*<img src={logo} className="App-logo" alt="logo" />*/}
            <p>
              Jarvis Workstation
            </p>
          </header>
          <body>
          <div className="NaviBar">
            {this.renderNaviBar()}
          </div>
          </body>
        </div>
    );
  }
}

class NaviBtn extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      hover: false
    }
  }

  triggerHover = () => {
    const hover = this.state.hover;
    console.log("chosen:" + this.props.activate);
    this.setState({
      hover: !hover
    })
  };

  render() {
    const background = !this.props.activate && this.state.hover ? '#555' : this.props.background;
    return (
        <div className='NaviBtn'>
          <button
              style = {{
                background: background,
              }}
              onMouseEnter={this.triggerHover}
              onMouseLeave={this.triggerHover}
              onClick={this.props.onClick}
          >{this.props.value}</button>
        </div>
    )
  }
}

export default App;
